import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import UserList from './components/UserList';
import UserForm from './components/UserForm';

function App() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch user list once on mount
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch users');
        return res.json();
      })
      .then(data => {
        setUsers(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  // Add user to state
  const addUser = (user) => {
    setUsers(prev => [...prev, user]);
  };

  // Update user in state
  const updateUser = (updatedUser) => {
    setUsers(prev => prev.map(user => user.id === updatedUser.id ? updatedUser : user));
  };

  // Delete user from state
  const deleteUser = (id) => {
    setUsers(prev => prev.filter(user => user.id !== id));
  };

  if (loading) return <div>Loading users...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        <header className="bg-blue-600 text-white p-4 flex justify-between items-center max-w-4xl mx-auto">
          <h1 className="text-xl font-semibold">User Management</h1>
          <nav>
            <Link to="/" className="mr-4 hover:underline">Home</Link>
            <Link to="/users/new" className="bg-green-500 px-3 py-1 rounded hover:bg-green-600">Add User</Link>
          </nav>
        </header>
        <main className="max-w-4xl mx-auto p-4">
          <Routes>
            <Route 
              path="/" 
              element={<UserList users={users} onDelete={deleteUser} />} 
            />
            <Route 
              path="/users/new" 
              element={<UserForm onAdd={addUser} />} 
            />
            <Route 
              path="/users/:id/edit" 
              element={<UserForm users={users} onUpdate={updateUser} />} 
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
